CREATE TABLE IF NOT EXISTS clients (id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, login text NOT NULL UNIQUE, password text NOT NULL);
CREATE TABLE IF NOT EXISTS accounts (id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, client_id FOREIGN KEY (client_id) REFERENCES clients(id);
CREATE TABLE IF NOT EXISTS transactions (uuid UUID PRIMARY KEY, debit_account FOREIGN KEY (id) REFERENCES accounts(id) NOT NULL, credit_account text NOT NULL);